<?php $loop_audio=get_option('bsa-loop-audio');$audio_file=get_option('bsa-custom-audio');$description=html_entity_decode(get_option('bsa-description'));$redirect=get_option('bsa-url');$tit=get_option('bsa-tit');$show_adv=get_option('bsa-adv');$image_adv=get_option('bsa-image');$time=get_option('bsa-time');$button=get_option('bsa-button');$color=get_option('bsa-color');$color_text=get_option('bsa-color-text');$opacity=get_option('bsa-opacity');$credits=get_option('bsa-credits');$time_action=get_option('bsa-time-action');$message = str_replace(array("\r\n", "\r", "\n", "\t"), ' ',  $description);$message = preg_replace(" {2,}", '<br />', $message);if ($show_adv==0){$show_sel="<div>" .$message. "</div>";}if ($show_adv==1){$show_sel="<img src=" . $image_adv . " height=\"25%\" width=\"25%\" alt=\"\" />";}$length = 7;$characters = '0123456789abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ';$randomString = 'bsa';for ($i = 0; $i < $length; $i++) {$randomString .= $characters[rand(0, strlen($characters) - 1)];}if ($time!=""){$time_button = "<input type=\"button\" onclick=\"$randomString.r(\'$randomString\')\" value=". $time. " disabled=\"true\">";}if ($time==""){$time_button = "<input type=\"hidden\" onclick=\"$randomString.r(\'$randomString\')\" value=". $time . " disabled=\"true\">";}if ($credits){$credits_aut = "<span id=\"bsa-credits\"><a title=\"Better Stop Adblock\" href=\"http://codeclan.altervista.org/\" target=\"_blank\">Better Stop Adblock</a></span>";}?>